export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK': {
      return [...state, action.urlData]
    }
    case 'DELETE_LINK':
      return state.filter(link => link.id !== action.id)
    default:
      return state
  }
}
