import styled from 'styled-components'

export const Wrapper = styled.header`
  display: grid;

  width: 100vw;
  height: 50vh;
`

export const Content = styled.div`
  position: relative;

  width: 80vw;
  max-width: 700px;

  justify-self: center;

  display: grid;

  @media (max-width: 700px) {
  }
`

export const TextSection = styled.div`
  position: relative;
  bottom: 15%;

  z-index: 1;

  display: grid;
  align-self: center;
`

export const Image = styled.img`
  position: absolute;
  bottom: 33%;

  justify-self: end;
  align-self: center;

  /* transform: translate(100%, 40%); */

  width: 40%;
  height: auto;
`

export const Title = styled.h1`
  font-size: 2rem;
  /* line-height: 3.5rem; */
  white-space: nowrap;

  color: #000646;
  span {
    color: #fff;
    background-color: #6c63ff;
  }
`
export const SubTitle = styled.p`
  kerning: 1.5rem;
  line-height: 1.25rem;
  max-width: 325px;
`
