import React, { useContext, useState } from 'react'

import UrlsContextProvider, { UrlsContext } from '../contexts/UrlsContext'
import UrlsList from '../components/UrlsList'

const IndexPage = () => {
  const { links, linksDispatch } = useContext(UrlsContext)

  const [url, setUrl] = useState('')

  // const handleClick = e => {
  //   linksDispatch({ type: 'ADD_LINK', link: { url } })
  // }

  const handleClick = async e => {
    const fetchData = await fetch('https://rel.ink/api/links/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        url:
          'https://www.frontendmentor.io/challenges/url-shortening-api-landing-page-2ce3ob-G'
      })
    }).then(data => data)

    console.log(fetchData)

    return fetchData
  }

  return (
    <div>
      <h1>Index Page</h1>
      <input
        type='text'
        placeholder='Url Title'
        value={url}
        onChange={e => setUrl(e.target.value)}
      />
      <button onClick={handleClick}>Click me!</button>
      <UrlsList />
    </div>
  )
}

export default IndexPage
