import styled from 'styled-components'

export const Wrapper = styled.section`
  padding: 5rem 0;
`

export const Content = styled.div`
  margin: 0 auto;
  width: 90%;
  max-width: 800px;

  display: flex;
  flex-direction: column;
`

export const Title = styled.h3`
  margin-bottom: 0;

  font-size: 2rem;
  font-weight: bold;
  letter-spacing: 0.1em;
`

export const Text = styled.p`
  margin-top: 1rem;
  max-width: 475px;

  font-family: Roboto;
  font-size: 18px;
  line-height: 200%;
  /* or 36px */

  text-align: justify;
  letter-spacing: 0.05em;

  color: #626262;
`

export const A = styled.a`
  font-family: Roboto;
  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 200%;
  /* identical to box height, or 28px */

  text-align: justify;
  text-decoration: none;
  letter-spacing: 0.135em;
`
