import styled from 'styled-components'

export const Wrapper = styled.section`
  position: relative;
  padding: 0vh 0 15vh 0;

  display: flex;

  align-items: center;
  justify-content: space-between;
  justify-self: center;

  width: 90%;
  max-width: 1140px;

  @media (max-width: 800px) {
    flex-direction: column-reverse;
  }
`

export const Content = styled.div`
  position: relative;
  right: -7.5%;

  max-width: 513px;

  display: flex;
  flex-direction: column;
`

export const Caret = styled.div`
  position: absolute;
  top: 38px;
  right: 15%;

  height: 26px;
  width: 8px;

  background-color: ${({ theme }) => theme.colors.secondary};
`

export const Title = styled.h3`
  margin-bottom: 0;

  font-size: 2rem;
  font-weight: bold;
  letter-spacing: 0.1em;
`

export const Text = styled.p`
  margin-top: 1rem;
  width: 85%;
  max-width: 475px;

  font-size: 14px;
  line-height: 200%;
  /* or 36px */

  text-align: justify;
  letter-spacing: 0.05em;

  color: #626262;
`
export const LinkWrapper = styled.div`
  display: flex;
  align-items: center;
`

export const A = styled.a`
  margin-right: 10px;

  color: ${({ theme }) => theme.colors.primary};

  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 200%;

  text-align: justify;
  text-decoration: none;
  letter-spacing: 0.135em;
`

export const Arrow = styled.svg`
  width: 16px;
  height: 16px;
`

export const Image = styled.img`
  width: 350px;
  height: 250px;
`

export const Lightning = styled.svg`
  position: absolute;
  left: -12.5%;
  top: 15%;

  transform: rotate(45deg) scaleX(-1);

  width: 100%;
  height: 100%;
`

export const L = styled.svg`
  position: absolute;
  right: -6%;
  bottom: 10%;

  transform: scaleX(-1);

  width: 20%;
  height: 20%;
`
