import React, { useState, useEffect, useContext } from 'react'

import UrlsContextProvider, { UrlsContext } from '../contexts/UrlsContext'
import UrlsList from '../components/UrlsList'

import fetchUrlData from '../lib/fetchUrlData'

const IndexPage = () => {
  const { links, linksDispatch } = useContext(UrlsContext)
  const [url, setUrl] = useState('')

  const fetchUrlData = async url => {
    const urlData = await fetch('https://rel.ink/api/links/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        url: url
      })
    })
      .then(res => res.json())
      .catch(err => err)

    return urlData
  }

  const handleClick = async url => {
    const urlData = await fetchUrlData(url)
    if (
      urlData.url == 'Enter a valid URL.' ||
      urlData.url == 'This field may not be blank.'
    ) {
      return
    }
    linksDispatch({ type: 'ADD_LINK', url: urlData })
  }

  return (
    <div>
      <h1>Index Page</h1>
      <input
        type='text'
        placeholder='Url'
        value={url}
        onChange={e => setUrl(e.target.value)}
      />
      <button onClick={() => handleClick(url)}>Click me!</button>
      <UrlsList />
    </div>
  )
}

export default IndexPage
