import styled from 'styled-components'

export const Wrapper = styled.header``

export const Content = styled.div`
  margin: 0 auto;
  position: relative;
  margin-top: 6%;

  width: 90%;
  max-width: 1140px;

  display: grid;

  @media (max-width: 800px) {
  }
`

export const Title = styled.h1`
  position: relative;
  margin-bottom: 1.5rem;

  text-align: center;
  font-size: 3rem;

  color: #000646;

  span {
    position: relative;
    margin-right: 0.25rem;
    display: inline-block;
    z-index: 2;

    span {
      position: relative;
      z-index: 1;
    }

    div {
      position: absolute;
      height: 0.833rem;
      height: 25%;
      background-color: #23e5af;
      width: 100%;
      bottom: 10%;
      left: 0;
      content: '';
      z-index: 0;
    }
  }
`
export const SubTitle = styled.p`
  text-align: center;
  font-size: 1.1rem;
  color: #3c4067;
`

export const LinksWrapper = styled.div`
  width: 100%;
  margin-top: 5%;

  display: flex;
  justify-content: center;
  justify-items: center;
  a:first-child {
    margin-right: 2rem;
  }

  @media (max-width: 800px) {
    max-width: 50%;
    flex-direction: column;

    a:first-child {
      margin-right: 0;
      margin-bottom: 2rem;
    }
  }
`

export const Link = styled.a`
  margin: 0;

  display: inline-flex;
  align-items: center;
  justify-content: center;

  padding: 0.8rem 1.8rem;

  text-decoration: none;
  text-align: center;
  font-weight: 600;
  font-size: 0.944rem;
  line-height: 1;

  color: ${({ primary }) => (!primary ? '#6062ff' : '#fff')};
  background-color: ${({ primary }) => (primary ? '#6062ff' : '#fff')};

  border: 1px solid #6062ff;
  border-radius: 2rem;
  cursor: pointer;
`
