export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK': {
      if (
        action.url == 'Enter a valid URL.' ||
        action.url == 'This field may not be blank.'
      ) {
        return
      }
      return [...state, action.url]
    }
    case 'DELETE_LINK':
      return state.filter(link => link.hashid !== action.id)
    case 'RESET':
      return []
    default:
      throw new Error('Unexpected action')
  }
}
