import React from 'react'
import styled from 'styled-components'

const HeaderWrapper = styled.header`
  display: grid;

  width: 100vw;
  height: 60vh;
`

const HeaderContent = styled.div`
  width: 80vw;
  justify-self: center;

  display: grid;
  grid-template-columns: 1fr 1fr;
`

const TextSection = styled.div`
  align-self: center;
`

const ImageSection = styled.div`
  align-self: center;
  justify-items: center;
`

const Image = styled.img`
  width: 50%;
  height: auto;
`

const Title = styled.h1`
  font-size: 3.5rem;
  /* white-space: nowrap; */
`
const SubTitle = styled.p``

const Header = () => {
  return (
    <HeaderWrapper>
      <HeaderContent>
        <TextSection>
          <Title>
            Get You Some <br />
            Shortened Links
          </Title>
          <SubTitle>Secure and reliable short links. Via rel.ink api.</SubTitle>
        </TextSection>
        <ImageSection>
          <Image src='/goochIcon.png' alt='gooch icon' width />
        </ImageSection>
      </HeaderContent>
    </HeaderWrapper>
  )
}

export default Header
