import React, { useState, useEffect, useContext } from 'react'
import styled from 'styled-components'

import { UrlsContext } from '../contexts/UrlsContext'
import fetchUrlData from '../lib/fetchUrlData'

import Header from '../components/Header'
import LinksList from '../components/LinksList'

const PageWrapper = styled.div`
  display: grid;
  grid-template-areas:
    'content'
    'screenshot';
`

const Form = styled.form``

const FormInput = styled.input``

const FormButton = styled.button``

const IndexPage = () => {
  const { links, linksDispatch } = useContext(UrlsContext)
  const [url, setUrl] = useState('')

  const handleSubmit = async e => {
    e.preventDefault()

    const urlData = await fetchUrlData(url)
    if (
      urlData.url == 'Enter a valid URL.' ||
      urlData.url == 'This field may not be blank.'
    ) {
      return
    }
    linksDispatch({ type: 'ADD_LINK', url: urlData })

    setUrl('')
  }

  return (
    <PageWrapper>
      <Form onSubmit={handleSubmit}>
        <FormInput
          type='text'
          placeholder='Url'
          value={url}
          onChange={e => setUrl(e.target.value)}
        />
        <FormButton onClick={e => handleSubmit(e)}>Click me!</FormButton>
      </Form>

      <LinksList />
    </PageWrapper>
  )
}

export default IndexPage
