import styled from 'styled-components'

export const UrlForm = styled.form`
  position: relative;
  top: 50%;

  justify-self: center;
  display: flex;
  justify-items: center;

  width: 75vw;
  min-width: 300px;
  max-width: 600px;

  border-radius: 5px;
`

export const FormInput = styled.input`
  font-family: Roboto;
  font-style: normal;
  font-weight: normal;
  font-size: 18px;
  line-height: 21px;
  /* identical to box height */

  color: #3e64ff;

  flex-grow: 1;
  padding: 10px;

  color: #3e64ff;
  border: 1px solid #3e64ff;
  border-radius: 8px 0 0 8px;

  line-height: 1;
`

export const FormButton = styled.button`
  /* margin-left: 40px; */

  padding: 1rem 2.5rem;

  background-color: #3e64ff;
  color: #fff;
  font-size: 0.9rem;
  font-weight: bold;
  white-space: nowrap;

  border: none;
  border-radius: 0 8px 8px 0;

  cursor: pointer;
`
