const fetchUrlData = async url => {
  const urlData = await fetch('https://rel.ink/api/links/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      url: url
    })
  }).then(res => res.json())

  console.log(urlData)

  return urlData
}

export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK':
      return [...state, fetchUrlData(action.url)]
    case 'DELETE_LINK':
      return state.filter(link => link.id !== action.id)
    default:
      return state
  }
}
