import styled from 'styled-components'

export const UrlForm = styled.form`
  position: relative;

  justify-self: center;
  display: flex;
  justify-items: center;

  width: 75vw;
  min-width: 300px;
  max-width: 600px;
  color: #3e4a49;
  background-color: #3f3d56;
  padding: 30px 35px;

  border-radius: 5px;
  font-family: 'Trebuchet MS', Arial, Helvetica, Sans-serif;
  font-size: 0.8em;
`

export const FormInput = styled.input`
  flex-grow: 1;
  padding: 10px;

  line-height: 1;
`

export const FormButton = styled.button`
  margin-left: 40px;

  padding: 1rem 2.5rem;

  background-color: #6c63ff;
  color: #fff;
  font-size: 0.9rem;
  font-weight: bold;
  white-space: nowrap;

  border: none;
  border-radius: 5px;
`
