import styled from 'styled-components'

export const UrlForm = styled.form`
  position: relative;
  top: 50%;

  justify-self: center;
  display: flex;
  justify-items: center;

  width: 75vw;
  min-width: 300px;
  max-width: 600px;
  color: #3e64ff;
  background-color: #3e64ff;

  border-radius: 5px;
  font-family: 'Trebuchet MS', Arial, Helvetica, Sans-serif;
  font-size: 0.8em;
`

export const FormInput = styled.input`
  flex-grow: 1;
  padding: 10px;

  background-color: #fff;
  border: none;

  line-height: 1;
`

export const FormButton = styled.button`
  /* margin-left: 40px; */

  padding: 1rem 2.5rem;

  background-color: #3e64ff;
  color: #fff;
  font-size: 0.9rem;
  font-weight: bold;
  white-space: nowrap;

  border: none;
  border-radius: 5px;

  cursor: pointer;
`
