import styled from 'styled-components'

export const Wrapper = styled.header`
  width: 100vw;

  display: grid;
  justify-content: center;
`

export const Content = styled.div`
  position: relative;
  margin-top: 6%;

  width: 95vw;
  max-width: 1140px;

  display: grid;

  @media (max-width: 800px) {
  }
`

export const Title = styled.h1`
  position: relative;
  margin-bottom: 1.5rem;

  text-align: center;
  font-size: 3rem;

  color: #000646;

  span {
    position: relative;
    margin-right: 0.25rem;
    display: inline-block;
    z-index: 2;

    span {
      position: relative;
      z-index: 1;
    }

    div {
      position: absolute;
      height: 0.833rem;
      height: 25%;
      background-color: #23e5af;
      width: 100%;
      bottom: 10%;
      left: 0;
      content: '';
      z-index: 0;
    }
  }
`
export const SubTitle = styled.p`
  text-align: center;
  font-size: 1.1rem;
  color: #3c4067;
`

export const LinksWrapper = styled.div`
  margin-top: 5%;

  display: flex;
  justify-content: center;
`

export const Link = styled.a`
  display: inline-flex;
  align-items: center;

  margin: 0 1rem;
  padding: 0.8rem 1.8rem;

  text-decoration: none;
  text-align: center;
  font-weight: 600;
  font-size: 0.944rem;
  line-height: 1;

  color: ${({ primary }) => (!primary ? '#6062ff' : '#fff')};
  background-color: ${({ primary }) => (primary ? '#6062ff' : '#fff')};

  border: 1px solid #6062ff;
  border-radius: 2rem;
  cursor: pointer;

  @media (max-width: 800px) {
    width: 100%;
  }
`
