import React, { useState, useEffect, useContext } from 'react'
import styled from 'styled-components'

import { UrlsContext } from '../contexts/UrlsContext'
import fetchUrlData from '../lib/fetchUrlData'

import LinksList from '../components/LinksList'

const PageWrapper = styled.div`
  display: grid;
  grid-template-areas:
    'content'
    'screenshot';
`

const Content = styled.div``

const Screenshot = styled.div``

const Header = styled.header`
  width: 100vw;
  height: 75vh;

  display: grid;
  grid-template-columns: 1fr 1fr;
`

const Title = styled.h1`
  font-size: 3rem;
`
const SubTitle = styled.p``

const Form = styled.form``

const FormInput = styled.input``

const FormButton = styled.button``

const IndexPage = () => {
  const { links, linksDispatch } = useContext(UrlsContext)
  const [url, setUrl] = useState('')

  const handleSubmit = async e => {
    e.preventDefault()

    const urlData = await fetchUrlData(url)
    if (
      urlData.url == 'Enter a valid URL.' ||
      urlData.url == 'This field may not be blank.'
    ) {
      return
    }
    linksDispatch({ type: 'ADD_LINK', url: urlData })

    setUrl('')
  }

  return (
    <PageWrapper>
      <Header>
        <div>
          <Title>
            Get You Some <br />
            Shortened Links
          </Title>
          <SubTitle>Secure and reliable short links. Via rel.ink api.</SubTitle>
        </div>
      </Header>

      <Form onSubmit={handleSubmit}>
        <FormInput
          type='text'
          placeholder='Url'
          value={url}
          onChange={e => setUrl(e.target.value)}
        />
        <FormButton onClick={e => handleSubmit(e)}>Click me!</FormButton>
      </Form>

      <LinksList />
    </PageWrapper>
  )
}

export default IndexPage
