import React, { createContext, useReducer, useEffect } from 'react'
import { urlsReducer } from '../reducers/UrlsReducer'

export const UrlsContext = createContext()

const UrlsContextProvider = ({ children }) => {
  const [urls, dispatch] = useReducer(urlsReducer, [])

  useEffect(() => {
    // localStorage && localStorage.setItem('urls', JSON.stringify(urls))
  }, [urls])

  return (
    <UrlsContext.Provider value={{ urls, dispatch }}>
      {children}
    </UrlsContext.Provider>
  )
}

export default UrlsContextProvider
