const fetchShortenedUrl = async url => {
  const fetchData = await fetch('https://rel.ink/api/links/', {
    headers: {
      'Content-Type': 'application/json'
    },
    body: { url }
  }).then(data => data)
}

export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK':
      return [...state, { url: action.link.url, id: Math.random() }]
    case 'DELETE_LINK':
      return state.filter(link => link.id !== action.id)
    default:
      return state
  }
}
