import styled from 'styled-components'

export const Wrapper = styled.header`
  width: 100vw;

  display: grid;
  justify-content: center;
`

export const Content = styled.div`
  position: relative;
  margin-top: 9%;

  width: 95vw;
  max-width: 1140px;

  display: grid;

  @media (max-width: 800px) {
  }
`

export const Title = styled.h1`
  text-align: center;
  font-size: 3rem;

  color: #000646;
`
export const SubTitle = styled.p`
  text-align: center;
  font-size: 1rem;
`

export const LinksWrapper = styled.div`
  display: flex;
  justify-content: center;
`

export const Link = styled.a`
  display: inline-flex;

  margin: 0 1rem;
  padding: 0.778rem 1.667rem 0.889rem 1.667rem;

  text-decoration: none;
  text-align: center;
  font-weight: 600;
  font-size: 0.944rem;

  color: #fff;
  background-color: #6062ff;

  border-radius: 2rem;
  cursor: pointer;
`
