export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK':
      return [...state, { link: action.link.url, id: Math.random() }]
    case 'DELETE_LINK':
      return state.filter(url => url.id !== action.id)
    default:
      return state
  }
}
