const fetchShortenedUrl = async url => {
  const fetchData = await fetch('https://rel.ink/api/links/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ url: url }) // body data type must match "Content-Type" header
  }).then(data => data)

  return fetchData
}

export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK':
      return [...state, fetchShortenedUrl(action.url)]
    case 'DELETE_LINK':
      return state.filter(link => link.id !== action.id)
    default:
      return state
  }
}
