import styled from 'styled-components'

export const Wrapper = styled.section`
  overflow-x: visible;
  padding: 15vh 0 7.5vh 0;

  display: grid;
  justify-self: center;
  grid-template-areas: 'content image';
  grid-template-columns: 1fr 1fr;

  width: 90%;
  max-width: 1140px;

  @media (max-width: 800px) {
    display: grid;
    grid-template-areas:
      'image'
      'content';
    grid-template-columns: 1fr;
    grid-template-rows: 1fr 1fr;
  }
`

export const Content = styled.div`
  position: relative;
  left: 25px;

  margin: 0 0;

  min-width: 350px;
  max-width: 475px;

  grid-area: content;
  align-self: center;

  display: flex;
  flex-direction: column;
`

export const Caret = styled.div`
  position: absolute;
  top: 5px;
  left: -25px;

  height: 26px;
  width: 8px;

  background-color: ${({ theme }) => theme.colors.secondary};
`

export const Title = styled.h3`
  margin-top: 0;
  margin-bottom: 0;

  font-size: 2rem;
  font-weight: bold;
  letter-spacing: 0.1em;
`

export const Text = styled.p`
  margin-top: 1rem;
  width: 85%;
  max-width: 475px;

  font-size: 14px;
  line-height: 200%;
  /* or 36px */

  text-align: justify;
  letter-spacing: 0.05em;

  color: #626262;
`
export const LinkWrapper = styled.div`
  display: flex;
  align-items: center;
`

export const A = styled.a`
  margin-right: 10px;

  color: ${({ theme }) => theme.colors.primary};

  font-style: normal;
  font-weight: bold;
  font-size: 14px;
  line-height: 200%;

  text-align: justify;
  text-decoration: none;
  letter-spacing: 0.135em;
`

export const Arrow = styled.svg`
  width: 16px;
  height: 16px;
`

export const Image = styled.img`
  width: 425px;
  height: 300px;

  grid-area: image;
  justify-self: center;
`

export const Ellipse = styled.svg`
  position: absolute;
  right: 5%;
  top: 52.5%;

  width: 20%;
  height: auto;

  transform: rotate(-180deg);
`
