const fetchShortenedUrl = async e => {
  const fetchData = await fetch('https://rel.ink/api/links/', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({
      url:
        'https://www.frontendmentor.io/challenges/url-shortening-api-landing-page-2ce3ob-G'
    })
  }).then(res => res.json())

  console.log(fetchData)

  return fetchData
}

export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK':
      return [...state, fetchShortenedUrl(action.url)]
    case 'DELETE_LINK':
      return state.filter(link => link.id !== action.id)
    default:
      return state
  }
}
