import React, { useContext, useState } from 'react'
import styled from 'styled-components'

import fetchUrlData from '../lib/fetchUrlData'

import { UrlsContext } from '../contexts/UrlsContext'

const UrlForm = styled.form`
  position: relative;
  top: 40px;

  justify-self: center;
  display: flex;
  justify-items: center;

  width: 500px;
  color: #3e4a49;
  background-color: #4b406b;
  padding: 25px;
  /* border: 2px solid #4899be; */
  border-radius: 5px;
  font-family: 'Trebuchet MS', Arial, Helvetica, Sans-serif;
  font-size: 0.8em;
`

const FormInput = styled.input`
  flex-grow: 1;
  padding: 20px;
`

const FormButton = styled.button`
  margin-left: 40px;
`

const Form = () => {
  const { linksDispatch } = useContext(UrlsContext)
  const [url, setUrl] = useState('')

  const handleSubmit = async e => {
    e.preventDefault()

    const urlData = await fetchUrlData(url)
    if (
      urlData.url == 'Enter a valid URL.' ||
      urlData.url == 'This field may not be blank.'
    ) {
      return
    }
    linksDispatch({ type: 'ADD_LINK', url: urlData })

    setUrl('')
  }

  return (
    <UrlForm onSubmit={handleSubmit}>
      <FormInput
        type='text'
        placeholder='Url'
        value={url}
        onChange={e => setUrl(e.target.value)}
      />
      <FormButton onClick={e => handleSubmit(e)}>Click me!</FormButton>
    </UrlForm>
  )
}

export default Form
