import fetchUrlData from '../lib/fetchUrlData'

export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_LINK': {
      return [...state, fetchUrlData(action.url)]
    }
    case 'DELETE_LINK':
      return state.filter(link => link.hashid !== action.id)
    case 'RESET':
      return []
    default:
      throw new Error('Unexpected action')
  }
}
