import styled from 'styled-components'

export const HeaderWrapper = styled.header`
  display: grid;

  width: 100vw;
  height: 50vh;
`

export const HeaderContent = styled.div`
  position: relative;

  width: 80vw;
  max-width: 700px;

  justify-self: center;

  display: grid;

  @media (max-width: 700px) {
  }
`

export const TextSection = styled.div`
  position: relative;
  top: 5%;

  z-index: 1;

  display: grid;
  align-self: center;
`

export const Image = styled.img`
  position: absolute;
  top: 33%;

  justify-self: end;
  align-self: center;

  /* transform: translate(100%, 40%); */

  width: auto;
  height: 40%;
`

export const Title = styled.h1`
  font-size: 2rem;
  /* line-height: 3.5rem; */
  white-space: nowrap;

  span {
    color: #fff;
    background-color: #6c63ff;
  }
`
export const SubTitle = styled.p`
  kerning: 1rem;
  line-height: 1.25rem;
  max-width: 325px;
`
