import React, { useContext, useEffect } from 'react'
import styled from 'styled-components'
import useClipboard from 'react-use-clipboard'

import { UrlsContext } from '../contexts/UrlsContext'

const ListWrapper = styled.ul`
  padding: 0 15%;

  display: flex;
  flex-direction: column-reverse;
`

const LinkItem = styled.li`
  display: flex;
  align-items: baseline;
  width: 100%;
`

const LinkUrl = styled.p`
  width: 250px;

  white-space: nowrap;
  text-overflow: ellipsis;
  flex-grow: 1;
`

const ShortenedLink = styled.a`
  white-space: nowrap;
`

const CopyIcon = styled.svg`
  position: relative;
  top: 2px;

  margin-left: 15px;
`

const Url = ({ link }) => {
  const shortenedLink = `https://rel.ink/${link.hashid}`

  const [isCopied, setCopied] = useClipboard(shortenedLink)

  return (
    <LinkItem>
      <LinkUrl>{link.url}</LinkUrl>
      <ShortenedLink href={shortenedLink}>{shortenedLink}</ShortenedLink>

      <CopyIcon
        onClick={setCopied}
        viewBox='0 0 14 16'
        version='1.1'
        width='14'
        height='16'
        aria-hidden='true'
      >
        <path
          fillRule='evenodd'
          d='M2 13h4v1H2v-1zm5-6H2v1h5V7zm2 3V8l-3 3 3 3v-2h5v-2H9zM4.5 9H2v1h2.5V9zM2 12h2.5v-1H2v1zm9 1h1v2c-.02.28-.11.52-.3.7-.19.18-.42.28-.7.3H1c-.55 0-1-.45-1-1V4c0-.55.45-1 1-1h3c0-1.11.89-2 2-2 1.11 0 2 .89 2 2h3c.55 0 1 .45 1 1v5h-1V6H1v9h10v-2zM2 5h8c0-.55-.45-1-1-1H8c-.55 0-1-.45-1-1s-.45-1-1-1-1 .45-1 1-.45 1-1 1H3c-.55 0-1 .45-1 1z'
        ></path>
      </CopyIcon>
    </LinkItem>
  )
}

const LinkListWrapper = styled.div`
  padding-top: 100px;

  width: 100vw;
  min-height: 30vh;

  background: #f0f1f6;
`

const LinksList = () => {
  const { links, linksDispatch } = useContext(UrlsContext)

  return (
    <LinkListWrapper>
      <ListWrapper>
        {links.length !== 0 ? (
          links.map(link => <Url link={link} key={link.hashid} />)
        ) : (
          <p>No urls here!</p>
        )}
      </ListWrapper>
    </LinkListWrapper>
  )
}

export default LinksList
