import styled from 'styled-components'

export const Wrapper = styled.header`
  width: 100vw;

  display: grid;
  justify-content: center;
`

export const Content = styled.div`
  position: relative;
  margin-top: 9%;

  width: 95vw;
  max-width: 1140px;

  display: grid;

  @media (max-width: 800px) {
  }
`

export const Title = styled.h1`
  display: block;
  margin-bottom: 1.5rem;

  text-align: center;
  font-size: 3rem;

  color: #000646;

  span ::after {
    position: absolute;
    display: flex;
    justify-content: center;

    height: 23%;
    background-color: #23e5af;
    width: 100%;
    bottom: 5%;
    left: 0;
    z-index: -1;

    content: '';
  }
`
export const SubTitle = styled.p`
  text-align: center;
  font-size: 1.1rem;
`

export const LinksWrapper = styled.div`
  margin-top: 5%;

  display: flex;
  justify-content: center;
`

export const Link = styled.a`
  display: inline-flex;
  align-items: center;

  margin: 0 1rem;
  padding: 0.778rem 1.667rem 0.889rem 1.667rem;
  transform: translateY(0px);

  text-decoration: none;
  text-align: center;
  font-weight: 600;
  font-size: 0.944rem;
  line-height: 1;

  color: ${({ primary }) => (!primary ? '#6062ff' : '#fff')};
  background-color: ${({ primary }) => (primary ? '#6062ff' : '#fff')};

  border: 1px solid #6062ff;
  border-radius: 2rem;
  cursor: pointer;
`
