import React, { useState, useEffect, useContext } from 'react'

import UrlsContextProvider, { UrlsContext } from '../contexts/UrlsContext'
import UrlsList from '../components/UrlsList'

const IndexPage = () => {
  const { links, linksDispatch } = useContext(UrlsContext)

  const [url, setUrl] = useState('')

  const handleClick = url => {
    linksDispatch({ type: 'ADD_LINK', link: { url } })
  }

  useEffect(() => {
    console.log(links)
  }, [links])

  return (
    <div>
      <h1>Index Page</h1>
      <input
        type='text'
        placeholder='Url Title'
        value={url}
        onChange={e => setUrl(e.target.value)}
      />
      <button onClick={() => linksDispatch({ type: 'ADD_LINK', url })}>
        Click me!
      </button>
      <UrlsList />
    </div>
  )
}

export default IndexPage
