export const urlsReducer = (state, action) => {
  switch (action.type) {
    case 'SET_LINKS': {
      const urlsList = action.localStorageUrls.map(url => url.toString())
      // console.log(urlsList)
      return urlsList == null ? [] : urlsList
    }
    case 'CLEAR_LINKS': {
      return []
    }
    case 'ADD_LINK': {
      return [
        ...state.filter(link => action.url.hashid !== link.hashid),
        action.url
      ]
    }
    case 'DELETE_LINK':
      return state.filter(link => link.hashid !== action.id)
    case 'RESET':
      return []
    default:
      throw new Error('Unexpected action')
  }
}
