import React, { useState, createContext, useReducer, useEffect } from 'react'
import { urlsReducer } from '../reducers/UrlsReducer'

export const UrlsContext = createContext()

const UrlsContextProvider = ({ children }) => {
  const [links, linksDispatch] = useReducer(urlsReducer, [])

  useEffect(() => {
    // const localStorageLinks = JSON.parse(localStorage.getItem('urls'))

    if (localStorageLinks == null) {
      localStorage.setItem(
        'urls',
        localStorage.getItem('urls', JSON.stringify([]))
      )
      // linksDispatch({ type: 'SET_LINKS', localStorageLinks })
    }
  }, [])

  return (
    <UrlsContext.Provider value={{ links, linksDispatch }}>
      {children}
    </UrlsContext.Provider>
  )
}

export default UrlsContextProvider
